mapper.select:

	select count(*)
	from groups
	where group_name = #{value}


	select count(*)
	from groups
	where group_url = #{value}

select rt.rn, rt.group_no, rt.province_no, rt.province, rt.region_no, rt.name, rt.category_no, rt.category_name, rt.group_name, rt.group_url, rt.content, rt.founded_date, rt.member_cnt from(select rownum rn, ot.group_no, ot.province_no, ot.province, ot.region_no, ot.name, ot.category_no, ot.category_name, ot.group_name, ot.group_url, ot.content, ot.founded_date, ot.member_cnt from (select gtbl.group_no, gtbl.province_no, gtbl.province, gtbl.region_no, gtbl.name, gtbl.category_no, gtbl.category_name, gtbl.group_name, gtbl.group_url, gtbl.group_content content, gtbl.founded_date, NVL(ctbl.member_cnt, 0) member_cnt from(select gs.group_no, gs.province_no, pv.province, gs.region_no, rg.name, gs.category_no, ct.category_name, gs.group_name, gs.group_url, gs.group_content, gs.founded_date from groups gs, province pv, region rg, category ct where gs.province_no = pv.province_no and gs.region_no = rg.region_no and gs.category_no = ct.category_no) gtbl , (select group_no, count(*) member_cnt from groupmember group by group_no) ctbl where gtbl.group_no = ctbl.group_no(+) order by gtbl.founded_date desc ) ot ) rt where rt.rn >=1 and rt.rn <=10
and rt.province_no = #{province_no}
and rt.region_no = #{region_no}
and rt.category_no = #{category_no}
and rt.group_name like '%'||#{gkeyword}||'%'
